import React, {Component} from "react";
import {render} from 'react-dom';
import { Link } from "react-router-dom";
import axios from "axios";
class ProductList extends Component{
    constructor(props){
        super(props);
        this.state = {
           
        }
    this.getProducts = this.getProducts.bind(this);
    this.deleteProduct = this.deleteProduct.bind(this);
    
    componentDidMount();{
        this.getProducts();
      }
        const getProducts = async () => {
            const response = await axios.get('http://localhost:5000/api/v1/tourism/');
            setProduct(response.data);
        }
     
        const deleteProduct = async (id) => {
            await axios.delete(`http://localhost:5000/api/v1/tourism/${id}`);
            getProducts();
        }


        
    };
render(){
    return(
        <div>
            <Link to="/add" className="button is-primary mt-2">New Data</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Place</th>
                        <th>Days</th>
                        <th>Package</th>
                        <th>Covid Restrictions</th>
                        <th>Temperature</th>
                        <th>Action</th>
                        
                    </tr>
                </thead>
                <tbody>
                    { products.map((product, index) => (
                        <tr key={ product.id }>
                            <td>{ index + 1 }</td>
                            <td>{ product.place }</td>
                            <td>{ product.days }</td>
                            <td>{ product.package }</td>
                            <td>{ product.covid_restrictions }</td>
                            <td>{ product.temperature }</td>
                            <td>
                                <Link to={`/edit/${product.id}`} className="button is-small is-info">Edit</Link>
                                <button onClick={ () =>deleteProduct(product.id) } className="button is-small is-danger">Delete</button>
                            </td>
                        </tr>
                    )) }
                     
                </tbody>
            </table>
        </div>
    )
}
}export default ProductList
