import React, { Component } from 'react';
import axios from "axios";
import { Link } from "react-router-dom";
import { withRouter } from "react-router";
 
class ProductList extends React.Component() {
    constructor (props) {
        super(props);
        this.state={
            count:0
        }
        this.handleButtonClick = this.handleButtonClick.bind(this);
    }
    handleButtonClick = () => {
        this.setState({count: this.state.count + 1 })
      };
    render(){
    const [products, setProduct] = setState([]);
 
    const getProducts = async () => {
        const response = await axios.get('http://localhost:5000/api/v1/tourism/');
        setProduct(response.data);
    }

    const deleteProduct = async (id) => {
        await axios.delete(`http://localhost:5000/api/v1/tourism/${id}`);
        getProducts();
    }
 
    return (
        <div>
            <Link to="/add" className="button is-primary mt-2">New Data</Link>
            <table className="table is-striped is-fullwidth">
                <thead>
                    <tr>
                        <th>No</th>
                        <th>Place</th>
                        <th>Days</th>
                        <th>Package</th>
                        <th>Covid Restrictions</th>
                        <th>Temperature</th>
                        <th>Action</th>
                        
                    </tr>
                </thead>
                <tbody>
                    { products.map((product, index) => (
                        <tr key={ product.id }>
                            <td>{ index + 1 }</td>
                            <td>{ product.place }</td>
                            <td>{ product.days }</td>
                            <td>{ product.package }</td>
                            <td>{ product.covid_restrictions }</td>
                            <td>{ product.temperature }</td>
                            <td>
                                <Link to={`/edit/${product.id}`} className="button is-small is-info">Edit</Link>
                            <button onClick={ () => deleteProduct (this.handleButtonClick.bind(this)) } className="button is-small is-danger">Delete</button> 
                            </td>
                        </tr>
                    )) }
                     
                </tbody>
            </table>
        </div>
    
    );
                    }}export default ProductList
