import React, { Component } from 'react';
import axios from "axios";
import { useNavigate} from 'react-router-dom';
import { render } from '@testing-library/react';
class AddProduct extends React.Component() {
    constructor (props) {
        super(props)
        this.state=this.props.stat
    }
    render(){
        const place = this.state.place;
      const setPlace = (params) => this.setState({place: params});
      const days = this.state.days;
      const setDays = (params) => this.setState({days: params});
      const packages = this.state.packages;
      const setPackages = (params) => this.setState({packages: params});
      const covid_restrictions = this.state.covid_restrictions;
      const setCovid_restrictions = (params) => this.setState({covid_restrictions: params});
      const temperature = this.state.temperature;
      const setTemperature = (params) => this.setState({temperature: params});
    // const navigate = useNavigate();
    const saveProduct = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:5000/api/v1/tourism',{
          place: place,
          days:days,
          package:packages,
          covid_restrictions:covid_restrictions,
          temperature:temperature
    });
       this.props.navigation.navigate("/");
    }

    return (
        <div>
            <form onSubmit={ saveProduct }>
 
                <div className="field">
                    <label className="label">PLACE</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="place"
                        value={ place }
                        onChange={ (e) => setPlace(e.target.value) }
                    />
                </div>
                <div className="field">
                    <label className="label">DAYS</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="days"
                        value={ days }
                        onChange={ (e) => setDays(e.target.value) }
                    />
                </div>
                <div className="field">
                    <label className="label">PACKAGE</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="packages"
                        value={ packages }
                        onChange={ (e) => setPackages(e.target.value) }
                    />
                </div>
                <div className="field">
                    <label className="label">COVID_RESTRICTIONS</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="covid_restrictions"
                        value={ covid_restrictions }
                        onChange={ (e) => setCovid_restrictions(e.target.value) }
                    />
                </div>
                <div className="field">
                    <label className="label">TEMPERATURE</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="temperature"
                        value={ temperature }
                        onChange={ (e) => setTemperature(e.target.value) }
                    />
                </div>
                <div className="field">
                    <button className="button is-primary">Save</button>
                </div>
            </form>
        </div>
    )}
}
 
export default AddProduct
