import React, { Component } from 'react';
import axios from "axios";

import { render } from '@testing-library/react';
 
class EditProduct extends React.Component() {
    constructor (props) {
        super(props)
        this.state=this.props.state
    }
   render() {
    const place = this.state.place;
    const setPlace = (params) => this.setState({place: params});
    const days = this.state.days;
    const setDays = (params) => this.setState({days: params});
    const packages = this.state.packages;
    const setPackages = (params) => this.setState({packages: params});
    const covid_restrictions = this.state.covid_restrictions;
    const setCovid_restrictions = (params) => this.setState({covid_restrictions: params});
    const temperature = this.state.temperature;
    const setTemperature = (params) => this.setState({temperature: params});
    // const navigate = useNavigate();
    const { id } = this.props.match.params.id;
    this.fetchData(id);
    const updateProduct = async (e) => {
        e.preventDefault();
        await axios.put(`http://localhost:5000/api/v1/tourism/${id}`,{
            place: place,
            days:days,
            package:packages,
            covid_restrictions:covid_restrictions,
            temperature:temperature
        });
       this.props.navigation. navigate("/");
    }
 
    useEffect(() => {
        getProductById();
    }, []); // eslint-disable-line react-hooks/exhaustive-deps
 
    const getProductById = async () => {
        const response = await axios.get(`http://localhost:5000/api/v1/tourism/${id}`);
        setPlace(response.data.place);
        setDays(response.data.days);
        setPackages(response.data.packages);
        setCovid_restrictions(response.data.covid_restrictions);
        setTemperature(response.data.temperature);

    }
 
    return (
        <div>
            <form onSubmit={ updateProduct }>
 
                <div className="field">
                    <label className="label">PLACE</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="Place"
                        value={ place }
                        onChange={ (e) => setPlace(e.target.value) }
                    />
                </div>
                <div className="field">
                    <label className="label">DAYS</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="days"
                        value={ days }
                        onChange={ (e) => setDays(e.target.value) }
                    />
                </div>
                <div className="field">
                    <label className="label">PACKAGES</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="packages"
                        value={ packages }
                        onChange={ (e) => setPackages(e.target.value) }
                    />
                </div>
                <div className="field">
                    <label className="label">COVID_RESTRICTIONS</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="covid_restrictions"
                        value={ covid_restrictions }
                        onChange={ (e) => setCovid_restrictions(e.target.value) }
                    />
                </div>
                <div className="field">
                    <label className="label">TEMPERATURE</label>
                    <input 
                        className="input"
                        type="text"
                        placeholder="temperature"
                        value={ temperature }
                        onChange={ (e) => setTemperature(e.target.value) }
                    />
                </div>
                <div className="field">
                    <button className="button is-primary">Update</button>
                </div>
            </form>
        </div>
    )
}}
 
export default EditProduct
