import React, {Component} from "react";
import {render} from 'react-dom';
import axios from "axios";
import { useNavigation } from 'react-navigation';

class AddProduct extends React.Component{
    constructor(props){
        super(props);
        this.state = {
             place:'',
             days:'',
             packages:'',
             covid_restrictions:'',
             temperature:''
        };
        this.saveProduct = this.saveProduct.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }
        render(){
        const {place,days,packages,covid_restrictions,temperature}=this.props;
        const navigation = useNavigation();
        const saveProduct = async (e) => {
            e.preventDefault();
            await axios.post('http://localhost:5000/api/v1/tourism',{
              place: place,
              days:days,
              package:packages,
              covid_restrictions:covid_restrictions,
              temperature:temperature
            });
            navigation("/");
        }   
    
        
    handleChange=(e)=>{
        this.setState=({[e.target.name]:e.target.value });
      }
      const inputchangehandler = (e) => {
        sethandleChange(e.target.value)
    }  
      this.setState({
          place:'',
          days:'',
          packages:'',
          covid_restrictions:'',
          temperature:''
      })
     return(
            <div>
            <form onSubmit={this.saveProduct }>
 
                <div className="field">
                    <label className="label">PLACE</label>
                    <input 
                        className="input"
                        type="text"
                        name="place"
                        placeholder="place"
                        value={ this.state.place }
                        onChange={this.handleChange}
                    />
                </div>
                <div className="field">
                    <label className="label">DAYS</label>
                    <input 
                        className="input"
                        type="text"
                        name="days"
                        placeholder="days"
                        value={this.state.days }
                        onChange={this.handleChange}

                    />
                </div>
                <div className="field">
                    <label className="label">PACKAGE</label>
                    <input 
                        className="input"
                        type="text"
                        name="packages"
                        placeholder="packages"
                        value={this.state.packages}
                        onChange={this.handleChange}
                    />
                </div>
                <div className="field">
                    <label className="label">COVID_RESTRICTIONS</label>
                    <input 
                        className="input"
                        type="text"
                        name="covid_restrictions"
                        placeholder="covid_restrictions"
                        value={ this.state.covid_restrictions }
                        onChange={this.handleChange}
                    />
                </div>
                <div className="field">
                    <label className="label">TEMPERATURE</label>
                    <input 
                        className="input"
                        type="text"
                        name="temperature"
                        placeholder="temperature"
                        value={ this.state.temperature }
                        onChange={this.handleChange}
                    />
                </div>
                <div className="field">
                    <button className="button is-primary">Save</button>
                </div>
            </form>
        </div>



    )

    }

 } export default AddProduct
