import React, {Component} from "react";
import {render} from 'react-dom';
import axios from "axios";
import { useNavigation } from 'react-navigation';

class EditProduct extends React.Component{
    constructor(props){
        super(props);
        this.state = {
             place:'',
             days:'',
             packages:'',
             covid_restrictions:'',
             temperature:''
              };
        this.handleChange = this.handleChange.bind(this);
        this.updateProduct = this.updateProduct.bind(this);
    }
    render(){   
        handleChange(e);{
            this.setState({[e.target.name]: e.target.value });
          }
          
          
          const {place,days,packages,covid_restrictions,temperature}=this.props;  
          const navigation = useNavigation();
          const updateProduct = async (e) => {
              e.preventDefault();
              await axios.put(`http://localhost:5000/api/v1/tourism/${id}`,{
                  place: place,
                  days:days,
                  packages:packages,
                  covid_restrictions:covid_restrictions,
                  temperature:temperature
              });
              navigation("/");
          }
       
          componentDidMount();{
            this.getProductById();
          }
          const getProductById = async () => {
              const response = await axios.get(`http://localhost:5000/api/v1/tourism/${id}`);
              setPlace(response.data.place);
              setDays(response.data.days);
              setPackages(response.data.packages);
              setCovid_restrictions(response.data.covid_restrictions);
              setTemperature(response.data.temperature);
       }
       this.setState({
        place:'',
        days:'',
        packages:'',
        covid_restrictions:'',
        temperature:''
    })
    return(
        <div>
        <form onSubmit={this.updateProduct }>

            <div className="field">
                <label className="label">PLACE</label>
                <input 
                    className="input"
                    type="text"
                    name="place"
                    placeholder="Place"
                    value={ this.state.place }
                    onChange={this.handleChange}
                />
            </div>
            <div className="field">
                <label className="label">DAYS</label>
                <input 
                    className="input"
                    type="text"
                    name="days"
                    placeholder="days"
                    value={this.state.days }
                    onChange={this.handleChange}
                />
            </div>
            <div className="field">
                <label className="label">PACKAGES</label>
                <input 
                    className="input"
                    type="text"
                    name="packages"
                    placeholder="packages"
                    value={this.state.packages}
                    onChange={this.handleChange}
                />
            </div>
            <div className="field">
                <label className="label">COVID_RESTRICTIONS</label>
                <input 
                    className="input"
                    type="text"
                    name="covid_restrictions"
                    placeholder="covid_restrictions"
                    value={ this.state.covid_restrictions }
                    onChange={this.handleChange}
                />
            </div>
            <div className="field">
                <label className="label">TEMPERATURE</label>
                <input 
                    className="input"
                    type="text"
                    name="temperature"
                    placeholder="temperature"
                    value={ this.state.temperature }
                    onChange={this.handleChange}
                />
            </div>
            <div className="field">
                <button className="button is-primary">Update</button>
            </div>
        </form>
    </div>
     )

}}export default EditProduct
